import * as potato from '@po-to/potato';

class Controller extends potato.Controller{
    
    Item(request: potato.Request, resolve: (data: Object | potato.ViewRendererData) => void, reject: (error: potato.PError) => void){
        let header: potato.Request = new potato.Request(request.root, 'm/common/Header', 'Item');
        let footer: potato.Request = new potato.Request(request.root, 'm/common/Footer', 'Item');
        let data = request.createViewRenderData({Header:header,Footer:footer});
        let template = new potato.View('m/joinSupplier/JoinSupplierTpl');
        let result = new potato.ViewRendererData(template,data);
        resolve(result);
    }
}

let ins:Object = new Controller();
export = ins;