import { IViews } from '@po-to/potato';

let map:{[key:string]:string} = require("`{VIEWSDIST}`/views.json");
let views: { [key: string]: string } = {};
for(let key in map){
    views['view://'+key] = require("`{VIEWSDIST}`/"+map[key]);
}

let ins: IViews = {
    getView:function(path:string):string|null{
        return views[path];
    },
    hasView:function(path:string):boolean{
        return (views as Object).hasOwnProperty(path);
    }
}

export = ins;